import React, { Component } from 'react';
import '../../assets/css/ControlCenter/ControlCenter.scss';
import Tabs from "./Tabs.js";
import StyleMenu from "./StyleMenu";
import ExportMenu from "./ExportMenu";

class ControlCenter extends Component {
    state = {
        currentTab: 'Style',
        currentName: ''
    }

    render() {
        if (this.state.currentName !== this.props.componentName) {
            const {getStyles} =  this.props;
            this.setState({currentName: this.props.componentName})
            return(
                <div className="control-center">
                    <Tabs currentTab={this.currentTab()}
                          switchTab={this.switchTab}/>
                    {this.currentTab() === 'Style' && <StyleMenu getStyles={getStyles}/>}
                    {this.currentTab() === 'Export' && <ExportMenu componentName={this.props.componentName}/>}
                </div>
            )
        }
    }

    switchTab = (tabName) => {
        this.setState({
            currentTab: tabName,
        })
    }

    currentTab = () => {
        return this.state.currentTab;
    }
}

export default ControlCenter;
