import React, { Component } from 'react';
import '../../assets/css/ControlCenter/StyleControl.scss';
import DropdownMenu from "./DropdownMenu";
import InputForm from "./InputForm";

class StyleControl extends Component {
    state = {
        fontSize: '',
        fontWeight: '',
        bgColor: '',
    }

    render () {
        const fontSize = ["Small", "Medium", "Large"]
        const fontWeight = ["Light", "Regular", "Bold"]

        return (
            <div className="style-control">
                <div className="style-control__inner">
                        <DropdownMenu label={"Font size"}
                                      elements={fontSize}
                                      returnValue={this.getElementStyle}/>
                        <DropdownMenu label={"Font weight"}
                                      elements={fontWeight}
                                      returnValue={this.getElementStyle}/>
                        <InputForm label={"Background Color"}
                                   returnValue={this.getElementStyle}/>
                </div>
            </div>
        )
    }

    getElementStyle = (element) => {
        switch (element) {
            case this.state.fontSize:
                this.setState({
                    fontSize: element
                })
                break;
            case this.state.fontWeight:
                this.setState({
                    fontWeight: element
                })
                break;
            default:
                this.setState({
                    bgColor: element
                })
        }
    }
}

export default StyleControl;
