let stylesObj = {};
let definedList = []
export function stylesCache (componentName, state) {
    if (definedList.indexOf(componentName) !== -1) {
        definedList.push(componentName);
        stylesObj.componentName = state;
    }
}

export function getStyle () {
    return stylesObj;
}
