import React, { Component } from 'react';
import '../../assets/css/ControlCenter/Tab.scss';

class TabBtn extends Component {
    constructor(props) {
        super(props);

        this.state = {
            isActive: false,
        }
    }

    render() {
        const {btnName} = this.props;
        const activeMarker = this.isActive() ? 'active' : 'disabled';

        return(
            <div className="nav__wrapper">
                <button className="nav__btn"
                        onClick={this.pressBtn}>
                    {btnName}
                </button>
                <div className={`nav__underline nav__underline_${activeMarker}`}/>
            </div>
        )
    }

    pressBtn = () => {
        this.setState({
            isActive: !this.isActive(),
        })
    }

    isActive = () => {
        return this.state.isActive;
    }
}


export default TabBtn;
