'use strict'

class StyleReader {
    constructor(className) {
        this.className = className;
        this._userClassName = '';
        this._style = {}
    }

    get userClassName() {
        this.decomposeClassName();
        return this._userClassName;
    }

    get style() {
        this.decomposeClassName();
        return this._style;
    }

    decomposeClassName() {
        const properties = this.className.split(' ');

        properties.forEach(property => {
            let propertyName = property.split("-")[0];
            let value = property.split("-")[1];

            switch (propertyName) {
                case 'fs':
                    this._style.fontSize = value;
                    break;
                case 'fw':
                    this._style.fontWeight = value;
                    break;
                case 'bg':
                    this._style.background = value;
                    break;
                case 'cl':
                    this._style.color = value;
                    break;
                default:
                    this._userClassName += property;
            }
        });
    }
}
