import React, {Component} from 'react';
import '../../assets/css/Test2.scss'

class Test2 extends Component {
    render() {
        const text = this.props.text;
        const color = this.props.color;
        const size = this.props.size;

        if (size === "small") {
            return (
                <button className="test-btn">{text}</button>
            )
        } else if (size === "medium") {
            return (
                <div>
                    <button className="test-btn" style={"font-size: 120%; font-family: Verdana, Arial, Helvetica, sans-serif; color: #336"}>{text}</button>
                </div>
            )
        } else {
            return (
                <button className="test-btn">{text}</button>
            )
        }
    }
}

export default Test2;
